sender_address = '0x3B3e452a5d1158F1Cd067ad73AD8298F9e5F2339'

token_address = web3.toChecksumAddress(input("token_address: "))




nonce = web3.eth.get_transaction_count(sender_address)

contract = web3.eth.contract(address=token_address, abi=panabi)

pancakeswap2_txn = contract.functions.approve(panRouterContractAddress, amount).buildTransaction({
'from': sender_address,
'gas': 1000000,
'gasPrice': web3.toWei('5','gwei'),
'nonce': nonce,
})





signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key=config.private)
tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
print(web3.toHex(tx_token))

